function getIntensity(sets: number): number {
  if (sets === 0) return 0
  return Math.min(sets / 12, 1)
}

// Render SVG paths for a body part
function renderBodyPart(
  bodyPart: { left?: string[]; right?: string[]; common?: string[] } | undefined,
  fill: string,
  opacity: number,
  strokeColor: string = '#666'
) {
  if (!bodyPart) return null

  const paths: React.ReactElement[] = []

  if (bodyPart.common) {
    bodyPart.common.forEach((d, i) => (
      paths.push(<path key={`common-${i}`} d={d} fill={fill} opacity={opacity} stroke={strokeColor} strokeWidth="0.5" />)
    ))
  }
  if (bodyPart.left) {
    bodyPart.left.forEach((d, i) => (
      paths.push(<path key={`left-${i}`} d={d} fill={fill} opacity={opacity} stroke={strokeColor} strokeWidth="0.5" />)
    ))
  }
  if (bodyPart.right) {
    bodyPart.right.forEach((d, i) => (
      paths.push(<path key={`right-${i}`} d={d} fill={fill} opacity={opacity} stroke={strokeColor} strokeWidth="0.5" />)
    ))
  }

  return paths
}

export default function MuscleHeatmap({ workoutSessions, profile }: MuscleHeatmapProps) {
  const muscleSets = useMemo(() => {
    const sets: Record<string, number> = {}
    MUSCLE_GROUPS.forEach(m => { sets[m.id] = 0 })

    Object.entries(workoutSessions).forEach(([exerciseId, sessions]) => {
      const exercise = exerciseId as Exercise
      const muscleMapping = EXERCISE_MUSCLES[exercise]
      if (!muscleMapping) return

      sessions.forEach(session => {
        const completedSets = session.sets.filter(s => s.weight && s.reps).length
        muscleMapping.primary.forEach(muscle => {
          sets[muscle] = (sets[muscle] || 0) + completedSets
        })
        muscleMapping.secondary.forEach(muscle => {
          sets[muscle] = (sets[muscle] || 0) + (completedSets * 0.5)
        })
      })
    })

    return sets
  }, [workoutSessions])

  const sortedMuscles = useMemo(() => {
    return [...MUSCLE_GROUPS].sort((a, b) => (muscleSets[b.id] || 0) - (muscleSets[a.id] || 0))
  }, [muscleSets])

  const maxSets = Math.max(...Object.values(muscleSets), 1)

  // Get fill color based on muscle intensity
  const getMuscleColor = (muscleId: string) => {
    const intensity = getIntensity(muscleSets[muscleId] || 0)
    if (intensity === 0) return '#e5e5e5'
    // Gradient from light cyan to deep cyan based on intensity
    const lightness = 70 - (intensity * 35)
    return `hsl(187, 85%, ${lightness}%)`
  }

  const getMuscleOpacity = (muscleId: string) => {
    const intensity = getIntensity(muscleSets[muscleId] || 0)
    return intensity === 0 ? 1 : 1
  }

  // Body base color
  const baseColor = '#d4d4d4'
  const baseStroke = '#a3a3a3'

  // Determine which body paths to use based on profile sex
  const isFemale = profile?.sex === 'female'
  const FRONT_PATHS = isFemale ? FEMALE_FRONT_PATHS : MALE_FRONT_PATHS
  const BACK_PATHS = isFemale ? FEMALE_BACK_PATHS : MALE_BACK_PATHS

  // ViewBox settings differ between male and female SVGs
  const frontViewBox = isFemale ? "60 80 560 1380" : "140 110 580 1200"
  const backViewBox = isFemale ? "900 0 500 1400" : "900 110 380 1200"

  return (
    <div className="bg-white dark:bg-gray-800 rounded-2xl p-5 shadow-sm">
      <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4 text-center">
        Muscle Distribution
      </h3>

      {/* Anatomical Body Views using react-native-body-highlighter paths */}
      <div className="flex justify-center items-start gap-4 mb-6 py-2">
        {/* Front View */}
        <div className="relative flex-shrink-0">
          <svg viewBox={frontViewBox} className="w-36 h-auto" style={{ maxHeight: '320px' }}>
            <defs>
              <linearGradient id="muscleGradient" x1="0%" y1="0%" x2="0%" y2="100%">
                <stop offset="0%" stopColor="#22d3ee" />
                <stop offset="100%" stopColor="#0891b2" />
              </linearGradient>
            </defs>

            {/* Render all body parts with highlighting */}
            {/* Head */}
            {renderBodyPart(FRONT_PATHS.head, baseColor, 1, baseStroke)}

            {/* Trapezius */}
            {renderBodyPart(FRONT_PATHS.trapezius, getMuscleColor('trapezius'), getMuscleOpacity('trapezius'), baseStroke)}

            {/* Deltoids/Shoulders */}
            {renderBodyPart(FRONT_PATHS.deltoids, getMuscleColor('deltoids'), getMuscleOpacity('deltoids'), baseStroke)}

            {/* Chest */}
            {renderBodyPart(FRONT_PATHS.chest, getMuscleColor('chest'), getMuscleOpacity('chest'), baseStroke)}

            {/* Biceps */}
            {renderBodyPart(FRONT_PATHS.biceps, getMuscleColor('biceps'), getMuscleOpacity('biceps'), baseStroke)}

            {/* Triceps */}
            {renderBodyPart(FRONT_PATHS.triceps, getMuscleColor('triceps'), getMuscleOpacity('triceps'), baseStroke)}

            {/* Abs */}
            {renderBodyPart(FRONT_PATHS.abs, getMuscleColor('abs'), getMuscleOpacity('abs'), baseStroke)}

            {/* Forearms */}
            {renderBodyPart(FRONT_PATHS.forearm, getMuscleColor('forearm'), getMuscleOpacity('forearm'), baseStroke)}

            {/* Quadriceps */}
            {renderBodyPart(FRONT_PATHS.quadriceps, getMuscleColor('quadriceps'), getMuscleOpacity('quadriceps'), baseStroke)}

            {/* Adductors */}
            {renderBodyPart(FRONT_PATHS.adductors, getMuscleColor('adductors'), getMuscleOpacity('adductors'), baseStroke)}

            {/* Calves */}
            {renderBodyPart(FRONT_PATHS.calves, getMuscleColor('calves'), getMuscleOpacity('calves'), baseStroke)}
          </svg>
          <p className="text-xs text-center text-gray-500 dark:text-gray-400 mt-1 font-medium">Front</p>
        </div>

        {/* Back View */}
        <div className="relative flex-shrink-0">
          <svg viewBox={backViewBox} className="w-36 h-auto" style={{ maxHeight: '320px' }}>
            {/* Head */}
            {renderBodyPart(BACK_PATHS.head, baseColor, 1, baseStroke)}

            {/* Trapezius */}
            {renderBodyPart(BACK_PATHS.trapezius, getMuscleColor('trapezius'), getMuscleOpacity('trapezius'), baseStroke)}

            {/* Deltoids */}
            {renderBodyPart(BACK_PATHS.deltoids, getMuscleColor('deltoids'), getMuscleOpacity('deltoids'), baseStroke)}

            {/* Upper Back */}
            {renderBodyPart(BACK_PATHS['upper-back'], getMuscleColor('upper-back'), getMuscleOpacity('upper-back'), baseStroke)}

            {/* Lower Back */}
            {renderBodyPart(BACK_PATHS['lower-back'], getMuscleColor('lower-back'), getMuscleOpacity('lower-back'), baseStroke)}

            {/* Triceps */}
            {renderBodyPart(BACK_PATHS.triceps, getMuscleColor('triceps'), getMuscleOpacity('triceps'), baseStroke)}

            {/* Forearms */}
            {renderBodyPart(BACK_PATHS.forearm, getMuscleColor('forearm'), getMuscleOpacity('forearm'), baseStroke)}

            {/* Glutes */}
            {renderBodyPart(BACK_PATHS.gluteal, getMuscleColor('gluteal'), getMuscleOpacity('gluteal'), baseStroke)}

            {/* Hamstrings */}
            {renderBodyPart(BACK_PATHS.hamstring, getMuscleColor('hamstring'), getMuscleOpacity('hamstring'), baseStroke)}

            {/* Calves */}
            {renderBodyPart(BACK_PATHS.calves, getMuscleColor('calves'), getMuscleOpacity('calves'), baseStroke)}
          </svg>
          <p className="text-xs text-center text-gray-500 dark:text-gray-400 mt-1 font-medium">Back</p>
        </div>
      </div>

      {/* Color Legend */}
      <div className="flex justify-center items-center gap-4 mb-4 text-xs">
        <div className="flex items-center gap-1.5">
          <div className="w-3 h-3 rounded-sm" style={{ backgroundColor: '#e5e5e5' }} />
          <span className="text-gray-500 dark:text-gray-400">No activity</span>
        </div>
        <div className="flex items-center gap-1.5">
          <div className="w-3 h-3 rounded-sm bg-cyan-300" />
          <span className="text-gray-500 dark:text-gray-400">Light</span>
        </div>
        <div className="flex items-center gap-1.5">
          <div className="w-3 h-3 rounded-sm bg-cyan-500" />
          <span className="text-gray-500 dark:text-gray-400">Moderate</span>
        </div>
        <div className="flex items-center gap-1.5">
          <div className="w-3 h-3 rounded-sm bg-cyan-700" />
          <span className="text-gray-500 dark:text-gray-400">Heavy</span>
        </div>
      </div>

      {/* Muscle List with Progress Bars */}
      <div className="border-t border-gray-200 dark:border-gray-700 pt-4">
        <div className="flex justify-between text-xs text-gray-500 dark:text-gray-400 mb-3 px-1 font-medium">
          <span>Muscle</span>
          <span>Completed Sets</span>
        </div>

        <div className="space-y-2 max-h-56 overflow-y-auto">
          {sortedMuscles.map(muscle => {
            const sets = muscleSets[muscle.id] || 0
            const percentage = maxSets > 0 ? (sets / maxSets) * 100 : 0

            return (
              <div key={muscle.id} className="flex items-center gap-3">
                <span className="text-sm text-gray-700 dark:text-gray-300 w-24 flex-shrink-0">
                  {muscle.name}
                </span>
                <div className="flex-1 h-3.5 bg-gray-100 dark:bg-gray-700 rounded-full overflow-hidden">
                  <div
                    className="h-full bg-gradient-to-r from-cyan-400 to-cyan-600 rounded-full transition-all duration-500"
                    style={{ width: `${Math.max(percentage, sets > 0 ? 5 : 0)}%` }}
                  />
                </div>
                <span className="text-sm font-medium text-gray-600 dark:text-gray-400 w-8 text-right tabular-nums">
                  {sets % 1 === 0 ? sets : sets.toFixed(1)}
                </span>
              </div>
            )
          })}
        </div>
      </div>
    </div>
  )
}
